        return a + b;
    }

    function subtract(a, b) {
        return a - b;
    }

    function multiply(a, b) {
        return a * b;
    }

    function divide(a, b) {
        return b !== 0 ? a / b : "Cannot divide by zero";
    }

    return {
        add: add,
        subtract: subtract,
        multiply: multiply,
        divide: divide
    };
})();

